				["init"] = {
					["do_custom"] = true,
					["custom"] = "aura_env.showAbsolute = false --set to true to show absolute heal value instead of percentage\n\naura_env.lastSec = 5\naura_env.percDmg = 0.5\naura_env.minHealthPerc = 0.05\n\naura_env.table = {}\n\naura_env.shortenNumber = function(number)\n    local affixes = {\n        \"\",\n        \"k\",\n        \"m\",\n        \"b\",\n    }\n    \n    local affix = 1\n    local dec = 0\n    while number >= 1000 and affix < #affixes do\n        number = number / 1000\n        affix = affix + 1\n    end\n    if affix > 1 then\n        dec = 2\n        local num2 = number\n        while num2 >= 10 do\n            num2 = num2 / 10\n            dec = dec - 1\n        end\n    end\n    \n    return string.format(\"%.\"..dec..\"f\"..affixes[affix], number)\nend\n\n--set bonuses\naura_env.GetNumSetPieces = function(set, class) \n    class = class or select(2, UnitClass(\"player\"))\n    local pieces = aura_env.sets[class][set] or {}\n    local counter = 0\n    for _, itemID in ipairs(pieces) do\n        if IsEquippedItem(itemID) then\n            counter = counter + 1\n        end\n    end\n    return counter\nend\n\naura_env.sets = {\n    [\"DRUID\"] = {\n        [\"T18\"] = {\n            124246,\n            124255,\n            124261,\n            124267,\n            124272,\n        },\n    },\n}",
