			["customText"] = "function()\n    local color = \"FFFFFFFF\"\n    local coloredName = function(name)\n        local _, class = UnitClass(name)\n        return string.format(\"|c%s%s\", RAID_CLASS_COLORS[class].colorStr, name)\n    end\n    function rangecheck(uId) -- using DBM function\n        local range = 0\n        if IsItemInRange(37727, uId) then range = 5\n        elseif IsItemInRange(63427, uId) then range = 8\n        elseif CheckInteractDistance(uId, 3) then range = 10\n        elseif CheckInteractDistance(uId, 2) then range = 11\n        elseif IsItemInRange(32321, uId) then range = 13\n        elseif IsItemInRange(6450, uId) then range = 18\n        elseif IsItemInRange(21519, uId) then range = 22\n        elseif CheckInteractDistance(uId, 1) then range = 30\n        elseif UnitInRange(uId) then range = 43\n        elseif IsItemInRange(116139, uId)  then range = 50\n        elseif IsItemInRange(32825, uId) then range = 60\n        elseif IsItemInRange(35278, uId) then range = 80\n        else range = 1000 end\n        return range\n    end\n    \n    local result = \"\"\n    local spell = 228744\n    local spell2 = 228810\n    local spell3 = 228818\n    local name, realm = UnitName(\"player\")\n    \n    for i=1,GetNumGroupMembers() do\n        local player = GetRaidRosterInfo(i)\n        if UnitDebuff(player, spell2) and UnitName(player) ~= name then\n            local distance = rangecheck(player)\n            if distance == 5 then\n                color = \"FFFF0000\" -- red\n                result = result..string.format(\"%s |c%s%s\", coloredName(player), color, \"- 0-8y\\n\")\n            elseif distance == 1000 then\n                color = \"FF00FF00\" -- green\n                result = result..string.format(\"%s |c%s%s\", coloredName(player), color\"- 80y\\n\")\n            elseif distance >= 18 then\n                color = \"FF00FF00\" -- green\n                result = result..string.format(\"%s |c%s%s%d%s\", coloredName(player), color, \"- >\", distance, \"y\\n\")\n            else\n                color = \"FFFF0000\" -- red\n                result = result..string.format(\"%s |c%s%s%d%s\", coloredName(player), color, \"- >\", distance, \"y\\n\")\n            end\n        end\n    end\n    return result\nend\n\n\n\n\n\n\n\n\n",
