			["displayText"] = "%c",
			["customText"] = "function()\n    local color = \"FFFFFFFF\"\n    local coloredName = function(name)\n        local _, class = UnitClass(name)\n        return string.format(\"|c%s%s\", RAID_CLASS_COLORS[class].colorStr, name)\n    end\n    function rangecheck(uId)\n        local range = 0\n        if IsItemInRange(37727, uId) then range = 5--Ruby Acorn\n        elseif IsItemInRange(63427, uId) then range = 8--Worgsaw\n        elseif IsItemInRange(32321, uId) then range = 13--reports 12 but actual range tested is 13\n        elseif IsItemInRange(33069, uId) then range = 15--Sturdy Rope\n        elseif IsItemInRange(10645, uId) then range = 20--Gnomish Death Ray\n        elseif IsItemInRange(24268, uId) then range = 25--Netherweave Net\n        elseif IsItemInRange(835  , uId) then range = 30--Large Rope Net\n        elseif IsItemInRange(24269, uId) then range = 35--Heavy Netherweave Net\n        elseif IsItemInRange(28767, uId) then range = 40--The Decapitator\n        elseif IsItemInRange(23836, uId) then range = 45--Goblin Rocket Launcher\n        else range = 1000 end--Just so it has a numeric value, even if it's unknown to protect from nil errors\n        return range\n    end\n    \n    local result = \"\"\n    \n    local distance = rangecheck(\"boss1\")\n    \n    if distance == 35 then\n        color = \"FF00FF00\" -- green\n        result = result..string.format(\"|c%s%s\", color, \"35-40y\\n\")\n    elseif distance == 40 or distance == 30 then\n        color = \"FFFFFF00\" -- yellow\n        if(distance == 40) then\n            result = result..string.format(\"|c%s%s\", color, \"40-45y\\n\")\n        else\n            result = result..string.format(\"|c%s%s\", color, \"30-35y\\n\")\n        end\n    else\n        color = \"FFFF0000\" -- red\n        if distance >= 45 then\n            result = result..string.format(\"|c%s%s\", color, \"45y+\")\n        else\n            result = result..string.format(\"|c%s%s\", color, \"0-30y\")\n        end\n    end\n    \n    return result\nend\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n",
