			["xOffset"] = -82.999755859375,
			["customText"] = "function()\n    local coloredName = function(name)\n        local _, class = UnitClass(name)\n        return string.format(\"|c%s%s\", RAID_CLASS_COLORS[class].colorStr, name)\n    end\n    local spell = GetSpellInfo(204463)\n    for i=1, GetNumGroupMembers() do\n        local name = GetRaidRosterInfo(i)\n        if UnitDebuff(name, spell) then\n            if name ~= UnitName(\"player\") then\n                local x,y = UnitPosition(name)\n                table.insert(aura_env.players, name)\n                table.insert(aura_env.xpositions, x)\n                table.insert(aura_env.ypositions, y)  \n                name, rank, icon, count, debuffType, duration, expires = UnitDebuff(name, spell)\n                duration = expires - GetTime()\n                table.insert(aura_env.duration, duration)  \n            end\n        end\n    end\n    \n    if table.getn(aura_env.players) == 1 then\n        result = \"\"\n        for i=1,1 do\n            local curX, curY = UnitPosition(\"player\")\n            local distSquared = (curX - aura_env.xpositions[i]) ^ 2 + (curY - aura_env.ypositions[i]) ^ 2\n            local dist = math.floor(distSquared^0.5)\n            \n            local color = \"FFFF0000\" -- red\n            if dist >= 60 then\n                color = \"FF00FF00\" -- green\n            end\n            loaded = IsAddOnLoaded(\"ExRT\")\n            if loaded == true then\n                local arrowTexture = GExRT.F.ArrowTextCoord(aura_env.ypositions[i], aura_env.xpositions[i])\n                result = result..string.format(\"%s |c%s%d%s\\n\", arrowTexture, color, dist, \"y  \")\n            else\n                result = result..string.format(\"|c%s%d%s\\n\", color..\" \", dist, \"y  \")\n            end\n        end\n        aura_env.players = {}\n        aura_env.xpositions = {}\n        aura_env.ypositions = {}\n        aura_env.duration = {}\n        return result\n    end\n    \nend\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\